mapper.select:
SELECT a.id,a.att_group_name,a.location,a.update_at, ( SELECT COUNT(1) FROM t_user u WHERE a.id = u.att_group_id ) AS userCounts FROM t_attendance_group a WHERE 1 = 1
and UPPER(a.att_group_name) like UPPER(CONCAT('%',#{queryParams.attGroupName,jdbcType=VARCHAR}, '%'))
order by a.id desc limit #{queryParams.page,jdbcType=INTEGER},#{queryParams.size,jdbcType=INTEGER}

SELECT t.id, t.name, s.company_name, g.group_name
,
( SELECT COUNT(1) FROM t_clock c WHERE c.user_id = t.id AND c.record_date = STR_TO_DATE(#{attDate}#{index},'%Y-%m-%d') AND c.clock_status = 1 ) AS clockCounts#{index}
FROM t_user t LEFT JOIN t_project p ON t.project_id = p.project_id LEFT JOIN t_subcontract s ON t.company_id = s.id LEFT JOIN t_group g ON t.goup_id = g.id LEFT JOIN t_work_type w ON t.work_type_id = w.id
and UPPER(t.name) like UPPER(CONCAT('%',#{queryParams.userName,jdbcType=VARCHAR}, '%'))
and t.goup_id = #{queryParams.groupID}
and g.principal_id = #{queryParams.principalID}
and t.company_id = #{queryParams.companyID}
and t.work_type_id = #{queryParams.workTypeID}
order by t.id desc limit #{queryParams.page,jdbcType=INTEGER},#{queryParams.size,jdbcType=INTEGER}

SELECT COUNT(1)
FROM t_user t
LEFT JOIN t_project p ON t.project_id = p.project_id
LEFT JOIN t_subcontract s ON t.company_id = s.id
LEFT JOIN t_group g ON t.goup_id = g.id
LEFT JOIN t_work_type w ON t.work_type_id = w.id
LEFT JOIN t_clock ck ON ck.user_id = t.id

    and UPPER(t.name) like UPPER(CONCAT('%',#{queryParams.userName,jdbcType=VARCHAR}, '%'))



    and t.goup_id = #{queryParams.groupID}



    and g.principal_id = #{queryParams.principalID}



    and t.company_id = #{queryParams.companyID}



    and t.work_type_id = #{queryParams.workTypeID}



    WHERE DATE_FORMAT(ck.record_date,'%Y-%m')=#{queryParams.attDate}

GROUP BY t.id
