mapper.select:
select count(t.ID) from ( SELECT t.id, t.name, t.certificate_type, t.certificate_number, t.gender, t.nation, t.birthday, t.address, t.project_id, t.company_id, t.goup_id, t.work_type_id, p.project_name, s.company_name, g.group_name, w.work_type_name, t.user_type, t.is_sick, t.phone, t.card_number, t.is_contract, t.head_img_id, t.contract_file_id, t.work_age, t.work_certificate, t.health, t.danger_operate, t.irregular_record, t.criticism, t.absence_work, t.creater, t.create_at, t.update_at FROM t_user t LEFT JOIN t_project p ON t.project_id = p.project_id LEFT JOIN t_subcontract s ON t.company_id = s.id LEFT JOIN t_group g ON t.goup_id = g.id LEFT JOIN t_work_type w ON t.work_type_id = w.id where 1 = 1
and UPPER(t.name) like UPPER(CONCAT('%',#{queryParams.name,jdbcType=VARCHAR}, '%'))
and t.certificate_number = #{queryParams.certificateNumber,jdbcType=INTEGER}
and t.phone = #{queryParams.phone,jdbcType=INTEGER}
and UPPER(t.goup_id) like UPPER(CONCAT('%',#{queryParams.goupID}, '%'))
and UPPER(t.work_type_id) like UPPER(CONCAT('%',#{queryParams.workType}, '%'))
) t

SELECT t.id, t.name, t.certificate_type, t.certificate_number, t.gender, t.nation, t.birthday, t.address, t.project_id, t.company_id, t.goup_id, t.work_type_id, p.project_name, s.company_name, g.group_name, w.work_type_name,
t.user_type, t.is_sick, t.phone, t.card_number, t.is_contract, t.contract_file_id, t.head_img_id, t.work_age, t.work_certificate, t.health,
t.danger_operate, t.irregular_record, t.criticism, t.absence_work, t.creater, t.create_at, t.update_at
FROM t_user t
LEFT JOIN t_project p ON t.project_id = p.project_id
LEFT JOIN t_subcontract s ON t.company_id = s.id
LEFT JOIN t_group g ON t.goup_id = g.id
LEFT JOIN t_work_type w ON t.work_type_id = w.id where 1 = 1

    and UPPER(t.name) like UPPER(CONCAT('%',#{queryParams.name,jdbcType=VARCHAR}, '%'))


    and t.certificate_number = #{queryParams.certificateNumber,jdbcType=INTEGER}


    and t.phone = #{queryParams.phone,jdbcType=INTEGER}


    and UPPER(t.goup_id) =#{queryParams.goupID}


    and UPPER(t.work_type_id) = #{queryParams.workTypeId}

order by ID desc
limit #{queryParams.page,jdbcType=INTEGER},#{queryParams.size,jdbcType=INTEGER}
